      });
      comp.checkConditions(data);
      if (!flags.noValidate) {
        valid &= comp.checkValidity(data, this.component.inlineEdit || !editRow.isOpen);
      }
    });

    if (!flags.noValidate) {
      valid &= (this.validateRow(editRow) === true);
    }

    // Trigger the change if the values changed.
    if (changed) {
