  it('Should allow you to set the input mask', (done) => {
    Harness.testBuilderProperty('inputMask', '', '(999) 999-9999', null, () => {
      assert.equal(builder.preview.inputs[0].placeholder, '(___) ___-____');
      builder.preview.setValue('1234567890');
