      }
    });

    valid = node.children.reduce((result, child) => this.checkNode(data, child, flags) && result, valid);

    // Trigger the change if the values changed.
    if (changed) {
      this.triggerChange(flags);
    }

    // Return if the value is valid.
    return valid;
  }
}

