    if (!Formio.tokens) {
      Formio.tokens = {};
    }

    if (Formio.tokens[tokenName]) {
      return Formio.tokens[tokenName];
    }
    try {
      Formio.tokens[tokenName] = localStorage.getItem(tokenName) || '';
      return Formio.tokens[tokenName];
    }
    catch (e) {
      Formio.tokens[tokenName] = cookies.get(tokenName);
